import React, { Component, useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import axios from 'axios';
import { InputTextarea } from 'primereact/inputtextarea';
import { Dropdown } from 'primereact/dropdown';

export default class Fibonacci extends Component{
    constructor(props){
        super(props)

        
        this.onChangeN = this.onChangeN.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.didGetResponse = this.didGetResponse.bind(this);
        
        this.state = {
            n : 0,
            fibonacciSequence : "",
            didSubmit:false,
        }
    }

    onChangeN(e) {
        this.setState({
          n: e.target.value
        })
        console.log("hola")
    }

    onSubmit(e) {
        console.log("aaaaaa1")
        const toGet = {
            n : this.state.n
          }
          axios
          .get('http://localhost:5000/fibonacci', toGet)
          .then(
              
              res => {
                  console.log(res.data);
                  if (res.data == "Please enter a valid number"){

                  }
                  else {
                    console.log("aaaaaa")
                    this.setState({
                        fibonacciSequence: res.data,
                        didSubmit : true
                      })
                  }
              }
          )
    }

    didGetResponse(){
        if (this.state.didSubmit == true){
            return <h3>The first {this.state.n} values of the fibonacci sequence are {this.state.fibonacciSequence}</h3>;
        }
        else {
            return <div></div>
        }
        

    }

    render() {
        return (
            <div className="card">
            <h5>Fibonacci</h5>
            <form onSubmit={this.onSubmit}> 
                <div className="formgroup-inline">
                    <div className="field">
                        <InputText id="firstname1" type="text" placeholder="N" onChange={this.onChangeN} />
                    </div>
                    
                        <Button label="Get sequence!" type="submit"></Button>
                    
                   
                </div>
                
            </form>
            <div>{this.didGetResponse()}</div>
            
        </div>
        );
    } 
}
